fn main() {
    println!("Part 1: {}\nPart 2: {}", include_str!("input.txt").lines().map(|line| line.parse::<i32>().unwrap()).collect::<Vec<i32>>().windows(2).filter(|nums| nums[0] < nums[1]).count(), include_str!("input.txt").lines().map(|line| line.parse::<i32>().unwrap()).collect::<Vec<i32>>().windows(3).map(|window| window.iter().sum::<i32>()).collect::<Vec<i32>>().windows(2).filter(|nums| nums[0] < nums[1]).count());
}